mapper.select:
SELECT a.id,a.is_stick,a.author,a.title,a.gmt_create,a.intro, (SELECT count(1) FROM blog_comment c WHERE a.id = c.article_id) AS commentCount FROM blog_article a
AND a.category_id = #{categoryId}
AND a.gmt_create > #{firstDay}
AND a.gmt_create < #{lastDay}
AND a.title LIKE CONCAT('%',trim(#{searchContent}),'%') OR a.author LIKE CONCAT('%',trim(#{searchContent}),'%')
ORDER BY a.is_stick DESC,a.id DESC

mapper.insert:
UPDATE blog_article
SET category_id  = #{categoryId}, title = #{title},intro = #{intro},author = #{author},is_stick = #{isStick},
is_draft=#{isDraft},allow_comment=#{allowComment},content=#{content}
WHERE id = #{id}
